"use strict";var Todo=require("../models/index");exports.getTodos=function(o,n){Todo.find().then(function(o){n.json(o)}).catch(function(o){n.send(o)})},exports.createTodos=function(o,n){Todo.create(o.body).then(function(o){n.status(201).json(o)}).catch(function(o){n.send(o)})},exports.showTodo=function(o,n){Todo.findById(o.params.todoId).then(function(o){n.json(o)}).catch(function(o){n.send(o)})},exports.updateTodo=function(o,n){Todo.findOneAndUpdate({_id:o.params.todoId},o.body,{new:!0}).then(function(o){n.json(o)}).catch(function(o){n.send(o)})},exports.delTodo=function(o,n){Todo.remove({_id:o.params.todoId}).then(function(){n.json({message:"Task deleted from the database!"})}).catch(function(o){n.send(o)})},module.exports=exports;